import React, { useState, useEffect } from 'react';
import axios from 'axios';
import zeptoVeg from '../images/zeptoVeggie.png';
import ZeptoPet from '../images/zeptoPetCare.png';
import ZeptoBaby from '../images/zeptoBabyCare.png';
import ZeptoHair from '../images/zeptoHaircare.png';
import ZeptoElectronics from '../images/zeptoElectronics.png';
import ZeptoIceCream from '../images/zeptoIceCream.png';
import ZeptoPackaged from '../images/zeptoPackagedFood.png';
import ZeptoCafe from '../images/zeptoCafe.png';
import ZeptoDairy from '../images/zeptoDairy.png';
import ZeptoFrozen from '../images/zeptoFrozen.png';
import ZeptoJewellery from '../images/zeptoJewellery.png';
import ZeptoSkincare from '../images/zeptoSkinCare.png';
import ZeptoTea from '../images/zeptoTea.png';
import ZeptoToys from '../images/zeptoToys.png';
import ZeptoAtta from '../images/zeptoAtta.png';
import zetpoBanner from '../images/zeptoHomeBanner.webp';
import zeptoEleBanner from '../images/zeptoEleBanner.webp';
import zeptoBeautyBanner from '../images/zeptoBeautyBanner.webp';
import { useCart } from '../contexts/cartContext';

function Home() {
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const { addToCart, removeFromCart, cartItems } = useCart();

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/customers');
      setProducts(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const electronicProducts = products.filter(
    (product) => product.ProductType === 'Electronic'
  );
  const BabyProducts = products.filter(
    (product) => product.ProductType === 'Baby'
  );
  const toyProducts = products.filter(
    (product) => product.ProductType === 'Toys'
  );
  const fashionProducts = products.filter(
    (product) => product.ProductType === 'Fashion'
  );
  const cafeProducts = products.filter(
    (product) => product.ProductType === 'Cafe'
  );
  const homeProducts = products.filter(
    (product) => product.ProductType === 'Home'
  );
  const mobileProducts = products.filter(
    (product) => product.ProductType === 'Mobile'
  );
  const beautyProducts = products.filter(
    (product) => product.ProductType === 'Beauty'
  );

  const ProductCard = ({ product }) => {
    const cartProduct = cartItems.find((item) => item.ProductID === product.ProductID);
    const quantity = cartProduct ? cartProduct.quantity : 0;

    return (
      <div className="col-lg-3 col-md-4 col-sm-6 mb-4">
        <div 
          className="card h-100 border-0 shadow-sm hover-shadow"
          onClick={() => setSelectedProduct(product)}
          style={{ cursor: 'pointer', transition: 'all 0.3s ease' }}
        >
          <div className="card-img-top-container" style={{ height: '200px', overflow: 'hidden' }}>
            <img
              src={`data:image/png;base64,${product.ProductImage}`}
              className="card-img-top img-fluid h-100 w-100"
              alt={product.ProductName}
              style={{ objectFit: 'contain', padding: '10px' }}
            />
          </div>
          <div className="card-body">
            <h5 className="card-title text-truncate">{product.ProductName}</h5>
            <p className="card-text text-muted small">Brand: {product.ProductBrand}</p>
            <p className="card-text fw-bold text-primary">₹{product.ProductPrice}</p>
            {(selectedCategory === 'Cafe' || selectedCategory === 'Electronics' || selectedCategory === 'All') && (
              cartProduct ? (
                <div className="d-flex align-items-center justify-content-between bg-light rounded-pill px-3 py-1">
                  <button
                    className="btn btn-sm btn-outline-danger rounded-circle"
                    onClick={(e) => {
                      e.stopPropagation();
                      removeFromCart(product.ProductID);
                    }}
                    style={{ width: '30px', height: '30px' }}
                  >
                    -
                  </button>
                  <span className="mx-2 fw-bold">{quantity}</span>
                  <button
                    className="btn btn-sm btn-outline-success rounded-circle"
                    onClick={(e) => {
                      e.stopPropagation();
                      addToCart(product);
                    }}
                    style={{ width: '30px', height: '30px' }}
                  >
                    +
                  </button>
                </div>
              ) : (
                <button
                  className="btn btn-outline-primary w-100 rounded-pill"
                  onClick={(e) => {
                    e.stopPropagation();
                    addToCart(product);
                  }}
                >
                  Add to Cart
                </button>
              )
            )}
          </div>
        </div>
      </div>
    );
  };

  const ProductModal = ({ product, onClose }) => (
    <div className="modal fade show d-block" tabIndex="-1" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
      <div className="modal-dialog modal-dialog-centered modal-lg">
        <div className="modal-content">
          <div className="modal-header border-0">
            <h5 className="modal-title">{product.ProductName}</h5>
            <button 
              type="button" 
              className="btn-close" 
              onClick={onClose}
              aria-label="Close"
            ></button>
          </div>
          <div className="modal-body">
            <div className="row">
              <div className="col-md-6">
                <img
                  src={`data:image/png;base64,${product.ProductImage}`}
                  alt={product.ProductName}
                  className="img-fluid rounded"
                  style={{ maxHeight: '300px', objectFit: 'contain' }}
                />
              </div>
              <div className="col-md-6">
                <div className="mb-3">
                  <h4 className="text-primary">₹{product.ProductPrice}</h4>
                  <p className="text-success mb-2">In Stock</p>
                </div>
                
                <div className="mb-3">
                  <h6>Description</h6>
                  <p>{product.ProductDescription || 'No description available'}</p>
                </div>
                
                <div className="row">
                  <div className="col-6 mb-2">
                    <small className="text-muted">Brand</small>
                    <p>{product.ProductBrand}</p>
                  </div>
                  <div className="col-6 mb-2">
                    <small className="text-muted">Quantity</small>
                    <p>{product.ProductQuantity}</p>
                  </div>
                  <div className="col-6 mb-2">
                    <small className="text-muted">Type</small>
                    <p>{product.ProductType}</p>
                  </div>
                  <div className="col-6 mb-2">
                    <small className="text-muted">Sub Type</small>
                    <p>{product.ProductSubType}</p>
                  </div>
                  <div className="col-6 mb-2">
                    <small className="text-muted">Weight</small>
                    <p>{product.ProductWeight}</p>
                  </div>
                  <div className="col-6 mb-2">
                    <small className="text-muted">Material</small>
                    <p>{product.ProductMaterial}</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="modal-footer border-0">
            <button 
              className="btn btn-primary rounded-pill px-4"
              onClick={() => {
                addToCart(product);
                onClose();
              }}
            >
              Add to Cart
            </button>
            <button 
              className="btn btn-outline-secondary rounded-pill px-4" 
              onClick={onClose}
            >
              Close
            </button>
          </div>
        </div>
      </div>
    </div>
  );

  return (
    <div className="home-container">
      {/* Category Navigation */}
      <div className="category-nav bg-white sticky-top shadow-sm py-2">
        <div className="container">
          <div className="d-flex overflow-auto hide-scrollbar">
            {['All', 'Cafe', 'Home', 'Toys', 'Fresh', 'Electronics', 'Mobiles', 'Beauty', 'Fashion', 'Deal', 'Baby'].map((category) => (
              <button
                key={category}
                className={`btn btn-sm mx-1 rounded-pill ${selectedCategory === category ? 'btn-primary' : 'btn-outline-secondary'}`}
                onClick={() => setSelectedCategory(category)}
              >
                {category}
              </button>
            ))}
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="container my-4">
        {selectedCategory === 'All' && (
          <>
            {/* Category Icons */}
            <div className="category-icons mb-4">
              <div className="d-flex overflow-auto hide-scrollbar py-2">
                {[
                  { img: zeptoVeg, name: 'Vegetables' },
                  { img: ZeptoPet, name: 'Pet Care' },
                  { img: ZeptoBaby, name: 'Baby Care' },
                  { img: ZeptoHair, name: 'Hair Care' },
                  { img: ZeptoElectronics, name: 'Electronics' },
                  { img: ZeptoIceCream, name: 'Ice Cream' },
                  { img: ZeptoPackaged, name: 'Packaged Food' },
                  { img: ZeptoCafe, name: 'Cafe' },
                  { img: ZeptoDairy, name: 'Dairy' },
                  { img: ZeptoFrozen, name: 'Frozen' },
                  { img: ZeptoJewellery, name: 'Jewellery' },
                  { img: ZeptoSkincare, name: 'Skincare' },
                  { img: ZeptoToys, name: 'Toys' },
                  { img: ZeptoTea, name: 'Tea' },
                  { img: ZeptoAtta, name: 'Atta' },
                ].map((item, index) => (
                  <div key={index} className="text-center mx-3">
                    <div className="bg-light rounded-circle p-3 mb-2">
                      <img
                        src={item.img}
                        alt={item.name}
                        style={{ width: '50px', height: '50px', objectFit: 'contain' }}
                      />
                    </div>
                    <small className="text-muted">{item.name}</small>
                  </div>
                ))}
              </div>
            </div>

            {/* Banner */}
            <div className="mb-4">
              <img 
                src={zetpoBanner} 
                alt="banner" 
                className="img-fluid rounded shadow-sm w-100"
                style={{ maxHeight: '200px', objectFit: 'cover' }}
              />
            </div>

            {/* Promotional Banners */}
            <div className="row mb-4">
              <div className="col-md-6 mb-3 mb-md-0">
                <img
                  src={zeptoEleBanner}
                  alt="Electronics Banner"
                  className="img-fluid rounded shadow-sm w-100"
                  style={{ height: '150px', objectFit: 'cover' }}
                />
              </div>
              <div className="col-md-6">
                <img
                  src={zeptoBeautyBanner}
                  alt="Beauty Banner"
                  className="img-fluid rounded shadow-sm w-100"
                  style={{ height: '150px', objectFit: 'cover' }}
                />
              </div>
            </div>
          </>
        )}

        {/* Products Section */}
        <div className="products-section">
          {selectedCategory === 'All' && (
            <>
              {electronicProducts.length > 0 && (
                <div className="mb-5">
                  <h4 className="mb-4 border-bottom pb-2">Electronics</h4>
                  <div className="row">
                    {electronicProducts.slice(0, 4).map((product) => (
                      <ProductCard key={product._id} product={product} />
                    ))}
                  </div>
                </div>
              )}

              {cafeProducts.length > 0 && (
                <div className="mb-5">
                  <h4 className="mb-4 border-bottom pb-2">Cafe Products</h4>
                  <div className="row">
                    {cafeProducts.slice(0, 4).map((product) => (
                      <ProductCard key={product._id} product={product} />
                    ))}
                  </div>
                </div>
              )}

              {BabyProducts.length > 0 && (
                <div className="mb-5">
                  <h4 className="mb-4 border-bottom pb-2">Baby Products</h4>
                  <div className="row">
                    {BabyProducts.slice(0, 4).map((product) => (
                      <ProductCard key={product._id} product={product} />
                    ))}
                  </div>
                </div>
              )}
            </>
          )}

          {selectedCategory === 'Electronics' && (
            <div className="mb-5">
              <h4 className="mb-4 border-bottom pb-2">Electronics</h4>
              <div className="row">
                {electronicProducts.map((product) => (
                  <ProductCard key={product._id} product={product} />
                ))}
              </div>
            </div>
          )}

          {selectedCategory === 'Cafe' && (
            <div className="mb-5">
              <h4 className="mb-4 border-bottom pb-2">Cafe Products</h4>
              <div className="row">
                {cafeProducts.map((product) => (
                  <ProductCard key={product._id} product={product} />
                ))}
              </div>
            </div>
          )}

          {selectedCategory === 'Baby' && (
            <div className="mb-5">
              <h4 className="mb-4 border-bottom pb-2">Baby Products</h4>
              <div className="row">
                {BabyProducts.map((product) => (
                  <ProductCard key={product._id} product={product} />
                ))}
              </div>
            </div>
          )}

          {selectedCategory === 'Toys' && (
            <div className="mb-5">
              <h4 className="mb-4 border-bottom pb-2">Toy Products</h4>
              <div className="row">
                {toyProducts.map((product) => (
                  <ProductCard key={product._id} product={product} />
                ))}
              </div>
            </div>
          )}

          {selectedCategory === 'Home' && (
            <div className="mb-5">
              <h4 className="mb-4 border-bottom pb-2">Home Products</h4>
              <div className="row">
                {homeProducts.map((product) => (
                  <ProductCard key={product._id} product={product} />
                ))}
              </div>
            </div>
          )}

          {selectedCategory === 'Mobiles' && (
            <div className="mb-5">
              <h4 className="mb-4 border-bottom pb-2">Mobile Products</h4>
              <div className="row">
                {mobileProducts.map((product) => (
                  <ProductCard key={product._id} product={product} />
                ))}
              </div>
            </div>
          )}

          {selectedCategory === 'Beauty' && (
            <div className="mb-5">
              <h4 className="mb-4 border-bottom pb-2">Beauty Products</h4>
              <div className="row">
                {beautyProducts.map((product) => (
                  <ProductCard key={product._id} product={product} />
                ))}
              </div>
            </div>
          )}

          {selectedCategory === 'Fashion' && (
            <div className="mb-5">
              <h4 className="mb-4 border-bottom pb-2">Fashion Products</h4>
              <div className="row">
                {fashionProducts.map((product) => (
                  <ProductCard key={product._id} product={product} />
                ))}
              </div>
            </div>
          )}
        </div>
      </div>

      {selectedProduct && (
        <ProductModal product={selectedProduct} onClose={() => setSelectedProduct(null)} />
      )}

      {/* Add some CSS for better UI */}
