import React, { useState } from 'react';

function Home() {
  const [location, setLocation] = useState('Click to get your location');

  const handleGetLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude, longitude } = position.coords;
          console.log('Latitude:', latitude);
          console.log('Longitude:', longitude);

          // Now fetch address from lat/lng
          const response = await fetch(
            `https://nominatim.openstreetmap.org/reverse?format=json&lat=${latitude}&lon=${longitude}`
          );
          const data = await response.json();
          console.log(data);
          setLocation(data.display_name);
        },
        (error) => {
          console.error(error);
          setLocation('Permission denied or unavailable');
        }
      );
    } else {
      setLocation('Geolocation not supported by your browser');
    }
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-lg-2">
          <button onClick={handleGetLocation} className="btn btn-primary">
            Get Current Location
          </button>
          <p>{location}</p>
        </div>
      </div>
    </div>
  );
}

export default Home;
