import React, { useState } from 'react';
import logo from '../images/zeptoLogo.svg';

function Home() {
  const [location, setLocation] = useState('');
  const [options, setOptions] = useState([]);

  const handleGetLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude, longitude } = position.coords;
          console.log('Latitude:', latitude);
          console.log('Longitude:', longitude);

          // Fetch address from lat/lng
          const response = await fetch(
            `https://nominatim.openstreetmap.org/reverse?format=json&lat=${latitude}&lon=${longitude}`
          );
          const data = await response.json();
          console.log(data);

          const address = data.display_name;
          setLocation(address);

          // Simulate some nearby locations for dropdown (you can customize)
          setOptions([
            address,
            'Koramangala, Bangalore',
            'HSR Layout, Bangalore',
            'Indiranagar, Bangalore',
          ]);
        },
        (error) => {
          console.error(error);
          setLocation('Permission denied or unavailable');
        }
      );
    } else {
      setLocation('Geolocation not supported');
    }
  };

  return (
    <div className="container-fluid">
      <div className="row align-items-center" style={{ minHeight: '100vh' }}>
        <div className="col-lg-2">
          <img src={logo} alt="logo" style={{ width: '100%', marginBottom: '20px' }} />

          {location ? (
            <select className="form-select" value={location} onChange={(e) => setLocation(e.target.value)}>
              {options.map((option, index) => (
                <option key={index} value={option}>
                  {option}
                </option>
              ))}
            </select>
          ) : (
            <button onClick={handleGetLocation} className="btn btn-primary">
              Detect Location
            </button>
          )}

          {location && (
            <div style={{ marginTop: '10px', fontSize: '14px', color: 'gray' }}>
              Selected: {location}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Home;
