import React, { useState } from "react";
import { auth } from "../firebase"; // Import from firebase.js
import { signInWithPhoneNumber,RecaptchaVerifier} from "firebase/auth";
import axios from 'axios';

function Register() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [otp, setOtp] = useState("");
  const [confirmationResult, setConfirmationResult] = useState(null);
  const [firebaseToken, setFirebaseToken] = useState("");
  
  // Set up reCAPTCHA for Firebase OTP
  const setupRecaptcha = () => {
    const recaptchaVerifier = new RecaptchaVerifier("recaptcha-container", {
      size: "invisible",
      callback: (response) => {
        console.log("reCAPTCHA solved", response);
      },
    }, auth);
    recaptchaVerifier.render();
  };

  // Send OTP to the user's phone number
  const sendOtp = () => {
    setupRecaptcha();
    const phoneNumberWithCountryCode = "+91" + phoneNumber; // Add country code if needed
    const appVerifier = window.recaptchaVerifier;

    signInWithPhoneNumber(auth, phoneNumberWithCountryCode, appVerifier)
      .then((confirmationResult) => {
        setConfirmationResult(confirmationResult);
        alert("OTP sent!");
      })
      .catch((error) => {
        console.error("Error during OTP sending: ", error);
        alert("Error sending OTP.");
      });
  };

  // Verify the OTP
  const verifyOtp = () => {
    if (confirmationResult) {
      confirmationResult
        .confirm(otp)
        .then((result) => {
          console.log("User signed in:", result.user);
          setFirebaseToken(result.user.uid);
          alert("OTP Verified! Proceed to registration.");
        })
        .catch((error) => {
          console.error("Error during OTP verification: ", error);
          alert("Invalid OTP.");
        });
    } else {
      alert("OTP has not been sent yet.");
    }
  };

  // Handle the registration process (including Firebase token)
  const handleRegister = async (e) => {
    e.preventDefault();

    if (!firebaseToken) {
      alert("Please verify your phone number with OTP first.");
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/auth/register', {
        name,
        email,
        password,
        role,
        firebaseToken
      });
      alert(response.data.message);
    } catch (error) {
      console.error("Registration error: ", error);
      alert("Error during registration.");
    }
  };

  return (
    <div>
      <h2>Register</h2>
      <form onSubmit={handleRegister}>
        <div>
          <input
            type="text"
            placeholder="Enter name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div>
          <input
            type="email"
            placeholder="Enter email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <input
            type="password"
            placeholder="Enter password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div>
          <select value={role} onChange={(e) => setRole(e.target.value)} required>
            <option value="">Select Role</option>
            <option value="user">User</option>
            <option value="admin">Admin</option>
          </select>
        </div>
        <div>
          <input
            type="text"
            placeholder="Enter phone number"
            value={phoneNumber}
            onChange={(e) => setPhoneNumber(e.target.value)}
            required
          />
          <button type="button" onClick={sendOtp}>Send OTP</button>
        </div>
        <div>
          <input
            type="text"
            placeholder="Enter OTP"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
            required
          />
          <button type="button" onClick={verifyOtp}>Verify OTP</button>
        </div>
        <button type="submit">Register</button>
      </form>
      <div id="recaptcha-container"></div>
    </div>
  );
}

export default Register;
