import React, { useState, useEffect } from 'react';
import logo from '../images/zeptoLogo.svg';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Navbar() {
    const [showDropdown, setShowDropdown] = useState(false);
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [products, setProducts] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [showSearchDropdown, setShowSearchDropdown] = useState(false);
    const [cartCount, setCartCount] = useState(0); // Add cart count state

    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        setIsLoggedIn(!!token);
        if (token) fetchCartCount();
    }, []);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const res = await axios.get('http://localhost:5000/api/customers');
                setProducts(res.data);
            } catch (err) {
                console.error('Error fetching products:', err);
            }
        };
        fetchProducts();
    }, []);

    const fetchCartCount = async () => {
      try {
        const token = localStorage.getItem('token');
        if (token) {
          const res = await axios.get('http://localhost:5000/api/cart', {
            headers: { Authorization: `Bearer ${token}` }
          });
          const count = (res.data || []).reduce((sum, item) => sum + item.quantity, 0);
          setCartCount(count);
        } else {
          setCartCount(0);
        }
      } catch (err) {
        console.error('Error fetching cart count:', err.response?.data || err.message);
        setCartCount(0);
      }
    };
    const handleIconClick = () => {
        setShowDropdown(!showDropdown);
    };

    const goToLogin = () => {
        navigate('/Login');
        setShowDropdown(false);
    };

    const goToRegister = () => {
        navigate('/Register');
        setShowDropdown(false);
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        setIsLoggedIn(false);
        setShowDropdown(false);
        setCartCount(0);
        alert('Logged out successfully!');
        navigate('/');
    };

    const redirectToHome = () => {
        navigate('/Home');
    };

    const handleSearchChange = (e) => {
        const value = e.target.value;
        setSearchTerm(value);
        if (!value.trim()) {
            setFilteredProducts([]);
            setShowSearchDropdown(false);
            return;
        }
        const filtered = products.filter(
            (product) =>
                product?.ProductName?.toLowerCase().includes(value.toLowerCase())
        );
        setFilteredProducts(filtered);
        setShowSearchDropdown(true);
    };

    const handleProductClick = (ProductID) => {
        setSearchTerm('');
        setShowSearchDropdown(false);
        navigate(`/product/${ProductID}`);
    };

    const goToCart = () => {
        navigate('/Cart'); // Assuming you have a Cart route
    };

    return (
        <div className='shadow-lg'>
            <div className='container-fluid shadow-lg p-4'>
                <div className='row align-items-center'>
                    <div className='col-lg-2'>
                        <img src={logo} alt='logo' width="120" onClick={redirectToHome} style={{ cursor: 'pointer' }} />
                    </div>
                    <div className='col-lg-2'>
                        <p>Location</p>
                    </div>
                    <div className='col-lg-6 position-relative'>
                        <input
                            className='form-control'
                            placeholder='Search'
                            type='text'
                            value={searchTerm}
                            onChange={handleSearchChange}
                        />
                        {showSearchDropdown && (
                            <ul className="position-absolute bg-white border rounded mt-1 w-100" style={{ zIndex: 1000 }}>
                                {filteredProducts.length > 0 ? (
                                    filteredProducts.map((product) => (
                                        <li
                                            key={product._id}
                                            className="p-2 border-bottom"
                                            style={{ cursor: 'pointer' }}
                                            onClick={() => handleProductClick(product.ProductID)}
                                        >
                                            {product.ProductName}
                                        </li>
                                    ))
                                ) : (
                                    <li className="p-2">No products found</li>
                                )}
                            </ul>
                        )}
                    </div>
                    <div className='col-lg-1 position-relative'>
                        <i
                            className="fa fa-user-circle-o fa-2x"
                            aria-hidden="true"
                            style={{ cursor: 'pointer' }}
                            onClick={handleIconClick}
                        ></i>
                        {showDropdown && (
                            <div className="position-absolute bg-white shadow rounded p-2" style={{ top: '50px', right: '0' }}>
                                {isLoggedIn ? (
                                    <p style={{ cursor: 'pointer' }} onClick={handleLogout}>Logout</p>
                                ) : (
                                    <>
                                        <p style={{ cursor: 'pointer' }} onClick={goToLogin}>Login</p>
                                        <p style={{ cursor: 'pointer' }} onClick={goToRegister}>Register</p>
                                    </>
                                )}
                            </div>
                        )}
                    </div>
                    <div className='col-lg-1 position-relative'>
                        <i
                            className="fa fa-shopping-cart fa-2x"
                            aria-hidden="true"
                            style={{ cursor: 'pointer' }}
                            onClick={goToCart}
                        ></i>
                        {cartCount > 0 && (
                            <span className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger">
                                {cartCount}
                            </span>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Navbar;
