// AdminDashboard.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AdminDashboard() {
  const [vendors, setVendors] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    vendorId: '',
    address: '',
    licenseNumber: '',
  });

  const [editId, setEditId] = useState(null);

  // Fetch vendors
  useEffect(() => {
    fetchVendors();
  }, []);

  const fetchVendors = async () => {
    const res = await axios.get('http://localhost:5000/api/vendors');
    setVendors(res.data);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    if (editId) {
      await axios.put(`http://localhost:5000/api/vendors/${editId}`, formData);
      setEditId(null);
    } else {
      await axios.post('http://localhost:5000/api/vendors', formData);
    }
    setFormData({ name: '', vendorId: '', address: '', licenseNumber: '' });
    fetchVendors();
  };

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:5000/api/vendors/${id}`);
    fetchVendors();
  };

  const handleEdit = (vendor) => {
    setFormData(vendor);
    setEditId(vendor._id);
  };

  return (
    <div>
      <h2>Admin Dashboard</h2>

      <div className="mb-4">
        <input name="name" placeholder="Vendor Name" value={formData.name} onChange={handleChange} />
        <input name="vendorId" placeholder="Vendor ID" value={formData.vendorId} onChange={handleChange} />
        <input name="address" placeholder="Address" value={formData.address} onChange={handleChange} />
        <input name="licenseNumber" placeholder="License No" value={formData.licenseNumber} onChange={handleChange} />
        <button onClick={handleSubmit}>{editId ? 'Update' : 'Add'} Vendor</button>
      </div>

      <div className="table-responsive">
        <table className="table table-bordered table-striped">
          <thead>
            <tr>
              <th>Sl No</th>
              <th>Vendor Name</th>
              <th>VendorID</th>
              <th>Vendor Address</th>
              <th>Vendor License Number</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {vendors.map((vendor, index) => (
              <tr key={vendor._id}>
                <td>{index + 1}</td>
                <td>{vendor.name}</td>
                <td>{vendor.vendorId}</td>
                <td>{vendor.address}</td>
                <td>{vendor.licenseNumber}</td>
                <td>
                  <button onClick={() => handleEdit(vendor)}>Update</button>
                  <button onClick={() => handleDelete(vendor._id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AdminDashboard;
