import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { auth, RecaptchaVerifier, signInWithPhoneNumber } from '../firebase';
function Register() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    role: '',
    phone: '',  // Phone number field
    otp: '',    // OTP field
    isOtpSent: false // To toggle OTP sent state
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle phone number submission to send OTP
  const handlePhoneNumberSubmit = async (e) => {
    e.preventDefault();
  
    let { phone } = formData;
  
    // Check if the phone number is valid and properly formatted
    if (!phone.startsWith('+')) {
      alert('Please enter a phone number with a valid country code (e.g., +1 for US, +91 for India).');
      return;
    }
  
    // Check if the phone number has a minimum length
    if (phone.length < 10) {
      alert('Phone number is too short. Please provide a valid phone number.');
      return;
    }
  
    const appVerifier = new RecaptchaVerifier('recaptcha-container', {
      size: 'invisible',
      callback: () => {},
    }, auth);
  
    try {
      await signInWithPhoneNumber(auth, phone, appVerifier);
      setFormData({ ...formData, isOtpSent: true });
      alert('OTP sent to your phone!');
    } catch (err) {
      console.error('Error during OTP sending:', err);
      alert('Failed to send OTP. Please try again.');
    }
  };
  
  
  // Handle final registration after OTP verification
  const handleSubmit = async (e) => {
    e.preventDefault();

    const { name, email, password, role, otp } = formData;

    try {
      // Confirm OTP entered by the user
      const confirmationResult = await auth.confirmationResult.confirm(otp);
      const firebaseToken = await confirmationResult.user.getIdToken();

      // Proceed with registration after OTP verification
      const response = await axios.post('http://localhost:5000/api/auth/register', {
        name,
        email,
        password,
        role,
        firebaseToken
      });

      alert('Registered successfully!');
      navigate('/login');
    } catch (err) {
      console.error('Registration error:', err);
      alert('Registration failed. Try again!');
    }
  };

  return (
    <div className="register-container d-flex justify-content-center align-items-center">
      <div className="card p-4 shadow register-card">
        <h2 className="text-center mb-4">Register</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <input 
              name="name" 
              className="form-control" 
              placeholder="Name" 
              value={formData.name}
              onChange={handleChange}
              required
            />
          </div>
          <div className="mb-3">
            <input 
              type="email" 
              name="email" 
              className="form-control" 
              placeholder="Email" 
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div className="mb-3">
            <input 
              type="password" 
              name="password" 
              className="form-control" 
              placeholder="Password" 
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          <div className="mb-3">
            <input 
              name="phone" 
              className="form-control" 
              placeholder="Phone Number" 
              value={formData.phone}
              onChange={handleChange}
              required
            />
          </div>
          <div id="recaptcha-container"></div>

          {/* Show OTP input field if OTP has been sent */}
          {!formData.isOtpSent ? (
            <div className="d-grid mb-3">
              <button type="button" onClick={handlePhoneNumberSubmit} className="btn btn-primary">
                Send OTP
              </button>
            </div>
          ) : (
            <div className="mb-3">
              <input 
                name="otp" 
                className="form-control" 
                placeholder="Enter OTP" 
                value={formData.otp}
                onChange={handleChange}
                required
              />
            </div>
          )}

          {/* Role selection */}
          <div className="mb-4">
            <select 
              name="role" 
              className="form-select"
              value={formData.role}
              onChange={handleChange}
              required
            >
              <option value="">Select Role</option>
              <option value="admin">Admin</option>
              <option value="vendor">Vendor</option>
              <option value="customer">Customer</option>
            </select>
          </div>

          {/* Submit button */}
          <div className="d-grid">
            <button type="submit" className="btn btn-success">Register</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Register;
