import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function ProductPage() {
  const { id } = useParams(); // Get the ProductID from the URL
  const [product, setProduct] = useState(null);

  useEffect(() => {
    const fetchProduct = async () => {
        try {
          const response = await axios.get(`http://localhost:5000/api/products/products/${id}`);
          setProduct(response.data);
        } catch (error) {
          console.error('Error fetching product:', error);
        }
      };
    fetchProduct();
  }, [id]); // Run the effect when the ID changes

  return (
    <div>
      {product ? (
        <div>
          <h1>{product.ProductName}</h1>
          <p>{product.ProductDescription}</p>
          <p>Price: {product.ProductPrice}</p>
          <p>Location: {product.ProductLocation}</p>
          <p>Quantity: {product.ProductQuantity}</p>
          {/* Render other fields like ProductType, ProductWeight, etc. */}
        </div>
      ) : (
        <p>Loading product details...</p>
      )}
    </div>
  );
}

export default ProductPage;
