// prevOrder.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const OrderHistory = () => {
  const [orders, setOrders] = useState([]);
  const email = localStorage.getItem("email"); // Use stored email after login

  useEffect(() => {
    if (email) {
      axios.get(`http://localhost:5000/api/orders/history/${email}`)
        .then(res => setOrders(res.data))
        .catch(err => console.error('Error:', err));
    }
  }, [email]);

  return (
    <div style={{ padding: "20px" }}>
      <h2>Your Orders</h2>
      {orders.length === 0 ? (
        <p>No orders found.</p>
      ) : (
        orders.map((order, idx) => (
          <div key={idx} style={{
            border: "1px solid #ccc",
            padding: "15px",
            marginBottom: "20px",
            borderRadius: "10px"
          }}>
            <h4>Order #{idx + 1} — ₹{order.total}</h4>
            <p><small>{new Date(order.createdAt).toLocaleString()}</small></p>
            <ul>
              {order.items.map((item, i) => (
                <li key={i}>
                  {item.ProductName} — {item.ProductQuantity} pcs @ ₹{item.ProductPrice}
                </li>
              ))}
            </ul>
          </div>
        ))
      )}
    </div>
  );
};

export default OrderHistory;
