import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Register() {
  const [step, setStep] = useState(1); // Step 1: Phone entry; Step 2: Full form
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    password: '',
    role: '',
    otp: ''
  });

  const [timer, setTimer] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    let interval;
    if (timer > 0) {
      interval = setInterval(() => {
        setTimer((prev) => prev - 1);
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [timer]);

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
  };

  const sendOtp = async () => {
    if (!/^\d{10}$/.test(formData.phone)) {
      alert('Please enter valid 10 digit phone number');
      return;
    }

    try {
      await axios.post('http://localhost:5000/api/auth/send-otp', { phone: formData.phone });
      alert('OTP sent successfully!');
      setStep(2);
      setTimer(30); // Start 30 second timer
    } catch (err) {
      console.error('Send OTP error:', err);
      alert('Failed to send OTP.');
    }
  };

  const resendOtp = async () => {
    try {
      await axios.post('http://localhost:5000/api/auth/send-otp', { phone: formData.phone });
      alert('OTP resent successfully!');
      setTimer(30); // Restart timer
    } catch (err) {
      console.error('Resend OTP error:', err);
      alert('Failed to resend OTP.');
    }
  };

  const register = async (e) => {
    e.preventDefault();

    const { name, email, password, role, phone, otp } = formData;

    if (name.trim().length < 3 || !/^[^\s@]+@[^\s@]+\.(com|in|net|org)$/.test(email) || password.length < 6 || role === '') {
      alert('Please fill all fields correctly.');
      return;
    }

    if (!otp) {
      alert('Please enter OTP.');
      return;
    }

    try {
      await axios.post('http://localhost:5000/api/auth/register', formData);
      alert('Registered successfully!');
      navigate('/login');
    } catch (err) {
      console.error('Register error:', err);
      alert('Registration failed.');
    }
  };

  return (
    <div className="register-container d-flex justify-content-center align-items-center">
      <div className="card p-4 shadow register-card">
        <h2 className="text-center mb-4">{step === 1 ? 'Verify Phone' : 'Register'}</h2>

        {step === 1 ? (
          <>
            <div className="mb-3">
              <input 
                type="text"
                name="phone"
                className="form-control"
                placeholder="Phone Number"
                value={formData.phone}
                onChange={handleChange}
                required
              />
            </div>
            <div className="d-grid">
              <button type="button" onClick={sendOtp} className="btn btn-primary">Send OTP</button>
            </div>
          </>
        ) : (
          <form onSubmit={register}>
            <div className="mb-3">
              <input 
                name="name" 
                className="form-control" 
                placeholder="Name" 
                value={formData.name}
                onChange={handleChange}
                required
              />
            </div>
            <div className="mb-3">
              <input 
                type="email" 
                name="email" 
                className="form-control" 
                placeholder="Email" 
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>
            <div className="mb-3">
              <input 
                type="password" 
                name="password" 
                className="form-control" 
                placeholder="Password" 
                value={formData.password}
                onChange={handleChange}
                required
              />
            </div>
            <div className="mb-3">
              <select 
                name="role" 
                className="form-select"
                value={formData.role}
                onChange={handleChange}
                required
              >
                <option value="">Select Role</option>
                <option value="admin">Admin</option>
                <option value="vendor">Vendor</option>
                <option value="customer">Customer</option>
              </select>
            </div>
            <div className="mb-3">
              <input 
                type="text"
                name="otp"
                className="form-control"
                placeholder="Enter OTP"
                value={formData.otp}
                onChange={handleChange}
                required
              />
            </div>

            <div className="mb-3 text-center">
              {timer > 0 ? (
                <p>Resend OTP in {timer} seconds</p>
              ) : (
                <button type="button" onClick={resendOtp} className="btn btn-link">Resend OTP</button>
              )}
            </div>

            <div className="d-grid">
              <button type="submit" className="btn btn-success">Register</button>
            </div>
          </form>
        )}
      </div>
    </div>
  );
}

export default Register;
