import React, { useState, useEffect } from 'react';
import { useCart } from '../contexts/cartContext';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Cart() {
  const {
    cartItems,
    addToCart,
    removeFromCart,
    clearCart,
    cartTotal,
    cartCount,
  } = useCart();

  const navigate = useNavigate();
  const [showAddressModal, setShowAddressModal] = useState(false);
  const [useSavedAddress, setUseSavedAddress] = useState(true);
  const [savedAddress, setSavedAddress] = useState(null);
  const [newAddress, setNewAddress] = useState({
    addressLine1: '',
    houseNo: '',
    building: '',
    landmark: '',
    label: 'Home',
  });

  const discount = cartTotal * 0.1;
  const totalAfterDiscount = cartTotal - discount;

  useEffect(() => {
    const fetchSavedAddress = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) return;

        const response = await axios.get('http://localhost:5000/api/customers/users/address', {
          headers: { Authorization: `Bearer ${token}` },
        });

        setSavedAddress(response.data.address);
      } catch (err) {
        console.error('Error fetching saved address:', err);
        const localAddress = JSON.parse(localStorage.getItem('userAddress'));
        if (localAddress) setSavedAddress(localAddress);
      }
    };

    fetchSavedAddress();
  }, []);

  const handleAddressChange = (e) => {
    const { name, value } = e.target;
    setNewAddress((prev) => ({ ...prev, [name]: value }));
  };

  const saveAddressAndProceed = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Please log in to proceed.');
        navigate('/Login');
        return;
      }

      let addressToUse = useSavedAddress && savedAddress ? savedAddress : newAddress;

      if (!useSavedAddress) {
        if (!newAddress.addressLine1 || !newAddress.houseNo || !newAddress.building) {
          alert('Please fill all required address fields.');
          return;
        }

        const response = await axios.put(
          'http://localhost:5000/api/customers/users/update-address',
          { address: newAddress },
          { headers: { Authorization: `Bearer ${token}` } }
        );

        setSavedAddress(newAddress);
        localStorage.setItem('userAddress', JSON.stringify(newAddress));
        addressToUse = newAddress;
      }

      localStorage.setItem('selectedAddress', JSON.stringify(addressToUse));
      setShowAddressModal(false);
      navigate('/Payment');
    } catch (err) {
      console.error('Save address failed:', err);
      alert('Failed to save address');
    }
  };

  const handleProceedToCheckout = () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please log in to continue');
      navigate('/Login');
      return;
    }

    setShowAddressModal(true);
  };

  return (
    <div className="container mt-4">
      <h2>Your Cart ({cartCount} items)</h2>

      {cartItems.length === 0 ? (
        <div className="alert alert-info">
          Your cart is empty.{' '}
          <button className="btn btn-link" onClick={() => navigate('/')}>
            Continue shopping
          </button>
        </div>
      ) : (
        <>
          <div className="row">
            <div className="col-lg-8">
              {cartItems.map((item) => (
                <div key={item.ProductID} className="card mb-3">
                  <div className="row g-0">
                    <div className="col-md-4">
                      <img
                        src={`data:image/png;base64,${item.ProductImage}`}
                        className="img-fluid rounded-start"
                        alt={item.ProductName}
                      />
                    </div>
                    <div className="col-md-8">
                      <div className="card-body">
                        <h5 className="card-title">{item.ProductName}</h5>
                        <p className="card-text">₹{item.ProductPrice}</p>
                        <div className="d-flex align-items-center">
                          <button className="btn btn-sm btn-outline-secondary" onClick={() => removeFromCart(item.ProductID)}>-</button>
                          <span className="mx-2">{item.quantity}</span>
                          <button className="btn btn-sm btn-outline-secondary" onClick={() => addToCart(item)}>+</button>
                          <span className="ms-auto fw-bold">
                            ₹{(item.quantity * item.ProductPrice).toFixed(2)}
                          </span>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>

            <div className="col-lg-4">
              <div className="card">
                <div className="card-body">
                  <h5>Order Summary</h5>
                  <div className="d-flex justify-content-between mb-2">
                    <span>Subtotal</span>
                    <span>₹{cartTotal.toFixed(2)}</span>
                  </div>
                  <div className="d-flex justify-content-between text-success">
                    <span>Discount</span>
                    <span>-₹{discount.toFixed(2)}</span>
                  </div>
                  <hr />
                  <div className="d-flex justify-content-between fw-bold">
                    <span>Total</span>
                    <span>₹{totalAfterDiscount.toFixed(2)}</span>
                  </div>
                  <button className="btn btn-primary w-100 mt-3" onClick={handleProceedToCheckout}>
                    Proceed to Checkout
                  </button>
                  <button className="btn btn-outline-danger w-100 mt-2" onClick={clearCart}>
                    Clear Cart
                  </button>
                </div>
              </div>
            </div>
          </div>

          {showAddressModal && (
            <div className="modal fade show d-block" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
              <div className="modal-dialog modal-dialog-centered">
                <div className="modal-content">
                  <div className="modal-header">
                    <h5>Delivery Address</h5>
                    <button className="btn-close" onClick={() => setShowAddressModal(false)}></button>
                  </div>
                  <div className="modal-body">
                    {savedAddress && useSavedAddress ? (
                      <>
                        <p><strong>{savedAddress.label}</strong>: {savedAddress.addressLine1}, {savedAddress.houseNo}, {savedAddress.building} {savedAddress.landmark ? `, ${savedAddress.landmark}` : ''}</p>
                        <div>
                          <button className="btn btn-sm btn-primary me-2" onClick={saveAddressAndProceed}>
                            Deliver Here
                          </button>
                          <button className="btn btn-sm btn-outline-primary" onClick={() => setUseSavedAddress(false)}>
                            Add New Address
                          </button>
                        </div>
                      </>
                    ) : (
                      <>
                        <div className="mb-2">
                          <input className="form-control" name="addressLine1" placeholder="Address Line 1" value={newAddress.addressLine1} onChange={handleAddressChange} />
                        </div>
                        <div className="mb-2">
                          <input className="form-control" name="houseNo" placeholder="House No" value={newAddress.houseNo} onChange={handleAddressChange} />
                        </div>
                        <div className="mb-2">
                          <input className="form-control" name="building" placeholder="Building/Block" value={newAddress.building} onChange={handleAddressChange} />
                        </div>
                        <div className="mb-2">
                          <input className="form-control" name="landmark" placeholder="Landmark (optional)" value={newAddress.landmark} onChange={handleAddressChange} />
                        </div>
                        <div className="mb-2">
                          <select className="form-select" name="label" value={newAddress.label} onChange={handleAddressChange}>
                            <option value="Home">Home</option>
                            <option value="Work">Work</option>
                            <option value="Other">Other</option>
                          </select>
                        </div>
                        <button className="btn btn-success w-100" onClick={saveAddressAndProceed}>
                          Save & Deliver
                        </button>
                      </>
                    )}
                  </div>
                </div>
              </div>
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default Cart;
