import React, { useState, useEffect } from 'react';
import axios from 'axios';
import zeptoVeg from '../images/zeptoVeggie.png';
import ZeptoPet from '../images/zeptoPetCare.png';
import ZeptoBaby from '../images/zeptoBabyCare.png';
import ZeptoHair from '../images/zeptoHaircare.png';
import ZeptoElectronics from '../images/zeptoElectronics.png';
import ZeptoIceCream from '../images/zeptoIceCream.png';
import ZeptoPackaged from '../images/zeptoPackagedFood.png';
import ZeptoCafe from '../images/zeptoCafe.png';
import ZeptoDairy from '../images/zeptoDairy.png';
import ZeptoFrozen from '../images/zeptoFrozen.png';
import ZeptoJewellery from '../images/zeptoJewellery.png';
import ZeptoSkincare from '../images/zeptoSkinCare.png';
import ZeptoTea from '../images/zeptoTea.png';
import ZeptoToys from '../images/zeptoToys.png';
import ZeptoAtta from '../images/zeptoAtta.png';
import zetpoBanner from '../images/zeptoHomeBanner.webp';
import zeptoEleBanner from '../images/zeptoEleBanner.webp';
import zeptoBeautyBanner from '../images/zeptoBeautyBanner.webp';
import { useCart } from '../contexts/cartContext';
import './Home.css'; // Add Zepto-inspired CSS

function Home() {
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const { addToCart, removeFromCart, cartItems } = useCart();

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/customers');
      setProducts(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const electronicProducts = products.filter(
    (product) => product.ProductType === 'Electronic'
  );
  const BabyProducts = products.filter(
    (product) => product.ProductType === 'Baby'
  );
  const toyProducts = products.filter(
    (product) => product.ProductType === 'Toys'
  );
  const fashionProducts = products.filter(
    (product) => product.ProductType === 'Fashion'
  );
  const cafeProducts = products.filter(
    (product) => product.ProductType === 'Cafe'
  );
  const homeProducts = products.filter(
    (product) => product.ProductType === 'Home'
  );
  const mobileProducts = products.filter(
    (product) => product.ProductType === 'Mobile'
  );
  const beautyProducts = products.filter(
    (product) => product.ProductType === 'Beauty'
  );

  const ProductCard = ({ product }) => {
    const cartProduct = cartItems.find((item) => item.ProductID === product.ProductID); // Check if product is in cart
    const quantity = cartProduct ? cartProduct.quantity : 0; // Get quantity from cart

    return (
      <div
        className="col-lg-3 zepto-card-col"
        onClick={() => setSelectedProduct(product)}
        style={{ cursor: 'pointer' }}
      >
        <div className="card h-100 zepto-card">
          <img
            src={`data:image/png;base64,${product.ProductImage}`}
            className="card-img-top"
            alt={product.ProductName}
            style={{ height: '200px', objectFit: 'cover' }}
          />
          <div className="card-body">
            <h5 className="card-title zepto-card-title">{product.ProductName}</h5>
            <p className="card-text zepto-card-text">Brand: {product.ProductBrand}</p>
            <p className="card-text zepto-card-price">â‚¹{product.ProductPrice}</p>
            {(selectedCategory === 'Cafe' || selectedCategory === 'Electronics' || selectedCategory === 'All') && (
              cartProduct ? (
                <div className="d-flex align-items-center">
                  <button
                    className="btn btn-sm btn-outline-success zepto-btn"
                    onClick={(e) => {
                      e.stopPropagation();
                      removeFromCart(product.ProductID);
                    }}
                  >
                    -
                  </button>
                  <span className="mx-2">{quantity}</span>
                  <button
                    className="btn btn-sm btn-outline-success zepto-btn"
                    onClick={(e) => {
                      e.stopPropagation();
                      addToCart(product);
                    }}
                  >
                    +
                  </button>
                </div>
              ) : (
                <button
                  className="btn btn-success zepto-btn-add"
                  onClick={(e) => {
                    e.stopPropagation();
                    addToCart(product);
                  }}
                >
                  Add to Cart
                </button>
              )
            )}
          </div>
        </div>
      </div>
    );
  };

  const ProductModal = ({ product, onClose }) => (
    <div
      className="modal zepto-modal"
      style={{
        display: 'block',
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: 'rgba(0,0,0,0.5)',
        zIndex: 1000,
      }}
    >
      <div
        className="modal-content zepto-modal-content"
        style={{
          backgroundColor: 'white',
          margin: '15% auto',
          padding: '20px',
          width: '70%',
          maxWidth: '500px',
          borderRadius: '5px',
        }}
      >
        <div className="modal-header">
          <h5 className="zepto-modal-title">{product.ProductName}</h5>
          <button className="btn btn-close" onClick={onClose}></button>
        </div>
        <div className="modal-body">
          <img
            src={`data:image/png;base64,${product.ProductImage}`}
            alt={product.ProductName}
            style={{ width: '100%', height: '200px', objectFit: 'cover', marginBottom: '15px' }}
          />
          <p className="zepto-modal-text">
            <strong>Description:</strong> {product.ProductDescription}
          </p>
          <p className="zepto-modal-text">
            <strong>Brand:</strong> {product.ProductBrand}
          </p>
          <p className="zepto-modal-text">
            <strong>Price:</strong> â‚¹{product.ProductPrice}
          </p>
          <p className="zepto-modal-text">
            <strong>Quantity:</strong> {product.ProductQuantity}
          </p>
          <p className="zepto-modal-text">
            <strong>Location:</strong> {product.ProductLocation}
          </p>
          <p className="zepto-modal-text">
            <strong>Type:</strong> {product.ProductType}
          </p>
          <p className="zepto-modal-text">
            <strong>Sub Type:</strong> {product.ProductSubType}
          </p>
          <p className="zepto-modal-text">
            <strong>Weight:</strong> {product.ProductWeight}
          </p>
          <p className="zepto-modal-text">
            <strong>Shelf:</strong> {product.ProductShelf}
          </p>
          <p className="zepto-modal-text">
            <strong>Material:</strong> {product.ProductMaterial}
          </p>
        </div>
        <div className="modal-footer">
          <button
            className="btn btn-success zepto-btn-add"
            onClick={() => {
              addToCart(product);
              onClose();
            }}
          >
            Add to Cart
          </button>
          <button className="btn btn-secondary zepto-btn-close" onClick={onClose}>
            Close
          </button>
        </div>
      </div>
    </div>
  );

  return (
    <div className="shadow-lg zepto-home">
      <div className="container-fluid shadow-lg zepto-nav">
        <div className="row">
          <div className="col-lg-1"></div>
          <div className="col-lg-9 d-flex justify-content-evenly">
            <a href="#" className="navTags zepto-nav-link" onClick={() => setSelectedCategory('All')}>
              All
            </a>
            <a href="#" className="navTags zepto-nav-link" onClick={() => setSelectedCategory('Cafe')}>
              Cafe
            </a>
            <a href="#" className="navTags zepto-nav-link" onClick={() => setSelectedCategory('Home')}>
              Home
            </a>
            <a href="#" className="navTags zepto-nav-link" onClick={() => setSelectedCategory('Toys')}>
              Toys
            </a>
            <a href="#" className="navTags zepto-nav-link" onClick={() => setSelectedCategory('Fresh')}>
              Fresh
            </a>
            <a href="#" className="navTags zepto-nav-link" onClick={() => setSelectedCategory('Electronics')}>
              Electronics
            </a>
            <a href="#" className="navTags zepto-nav-link" onClick={() => setSelectedCategory('Mobiles')}>
              Mobiles
            </a>
            <a href="#" className="navTags zepto-nav-link" onClick={() => setSelectedCategory('Beauty')}>
              Beauty
            </a>
            <a href="#" className="navTags zepto-nav-link" onClick={() => setSelectedCategory('Fashion')}>
              Fashion
            </a>
            <a href="#" className="navTags zepto-nav-link" onClick={() => setSelectedCategory('Deal')}>
              Deal Zone
            </a>
            <a href="#" className="navTags zepto-nav-link" onClick={() => setSelectedCategory('Baby')}>
              Baby Store
            </a>
          </div>
        </div>
      </div>

      <div className="row zepto-content">
        {selectedCategory === 'All' && (
          <div className="col-lg-12">
            <div style={{ overflowX: 'auto', whiteSpace: 'nowrap', padding: '10px' }} className="zepto-category-scroll">
              <img
                src={zeptoVeg}
                alt="Vegetables"
                style={{ width: '100px', height: '100px', marginRight: '20px', objectFit: 'contain' }}
              />
              <img
                src={ZeptoPet}
                alt="Pet"
                style={{ width: '100px', height: '100px', marginRight: '20px', objectFit: 'contain' }}
              />
              <img
                src={ZeptoBaby}
                alt="Baby"
                style={{ width: '100px', height: '100px', marginRight: '20px', objectFit: 'contain' }}
              />
              <img
                src={ZeptoHair}
                alt="Hair"
                style={{ width: '100px', height: '100px', marginRight: '20px', objectFit: 'contain' }}
              />
              <img
                src={ZeptoElectronics}
                alt="Electronics"
                style={{ width: '100px', height: '100px', marginRight: '20px', objectFit: 'contain' }}
              />
              <img
                src={ZeptoIceCream}
                alt="Ice Cream"
                style={{ width: '100px', height: '100px', marginRight: '20px', objectFit: 'contain' }}
              />
              <img
                src={ZeptoPackaged}
                alt="Packaged"
                style={{ width: '100px', height: '100px', marginRight: '20px', objectFit: 'contain' }}
              />
              <img
                src={ZeptoCafe}
                alt="Cafe"
                style={{ width: '100px', height: '100px', marginRight: '20px', objectFit: 'contain' }}
              />
              <img
                src={ZeptoDairy}
                alt="Cafe"
                style={{ width: '100px', height: '100px', marginRight: '20px', objectFit: 'contain' }}
              />
              <img
                src={ZeptoFrozen}
                alt="Cafe"
                style={{ width: '100px', height: '100px', marginRight: '20px', objectFit: 'contain' }}
              />
              <img
                src={ZeptoJewellery}
                alt="Cafe"
                style={{ width: '100px', height: '100px', marginRight: '20px', objectFit: 'contain' }}
              />
              <img
                src={ZeptoSkincare}
                alt="Cafe"
                style={{ width: '100px', height: '100px', marginRight: '20px', objectFit: 'contain' }}
              />
              <img
                src={ZeptoToys}
                alt="Cafe"
                style={{ width: '100px', height: '100px', marginRight: '20px', objectFit: 'contain' }}
              />
              <img
                src={ZeptoTea}
                alt="Cafe"
                style={{ width: '100px', height: '100px', marginRight: '20px', objectFit: 'contain' }}
              />
              <img
                src={ZeptoAtta}
                alt="Cafe"
                style={{ width: '100px', height: '100px', marginRight: '20px', objectFit: 'contain' }}
              />
            </div>
            <div className="col-lg-12">
              <img className="mt-5 zepto-banner" src={zetpoBanner} alt="banner" />
            </div>
            <div className="container-fluid mt-4 mb-4">
              <div className="row">
                <div className="col-lg-6">
                  <img
                    src={zeptoEleBanner}
                    alt="banner"
                    className="img-fluid zepto-sub-banner"
                    style={{ width: '100%', height: 'auto' }}
                  />
                </div>
                <div className="col-lg-6">
                  <img
                    src={zeptoBeautyBanner}
                    alt="banner"
                    className="img-fluid zepto-sub-banner"
                    style={{ width: '100%', height: 'auto' }}
                  />
                </div>
              </div>
            </div>
          </div>
        )}

        {(selectedCategory === 'Cafe' || selectedCategory === 'All') && (
          <>
            <div className="col-lg-12 mb-4">
              <h2 className="zepto-section-title">Cafe Products</h2>
            </div>
            {cafeProducts.length > 0 ? (
              <div className="row">
                {cafeProducts.map((product) => (
                  <ProductCard key={product._id} product={product} />
                ))}
              </div>
            ) : (
              <div className="col-lg-12">
                <p className="zepto-no-items">No cafe products available.</p>
              </div>
            )}
          </>
        )}

        {selectedCategory === 'Home' && (
          <>
            <div className="col-lg-12 mb-4">
              <h2 className="zepto-section-title">Home Products</h2>
            </div>
            {homeProducts.length > 0 ? (
              <div className="row">
                {homeProducts.map((product) => (
                  <ProductCard key={product._id} product={product} />
                ))}
              </div>
            ) : (
              <div className="col-lg-12">
                <p className="zepto-no-items">No home products available.</p>
              </div>
            )}
          </>
        )}

        {selectedCategory === 'Toys' && (
          <>
            <div className="col-lg-12 mb-4">
              <h2 className="zepto-section-title">Toy Products</h2>
            </div>
            {toyProducts.length > 0 ? (
              <div className="row">
                {toyProducts.map((product) => (
                  <ProductCard key={product._id} product={product} />
                ))}
              </div>
            ) : (
              <div className="col-lg-12">
                <p className="zepto-no-items">No toy products available.</p>
              </div>
            )}
          </>
        )}

        {selectedCategory === 'Fresh' && (
          <div className="col-lg-12">
            <p className="zepto-no-items">Fresh</p>
          </div>
        )}

        {(selectedCategory === 'Electronics' || selectedCategory === 'All') && (
          <>
            <div className="col-lg-12 mb-4">
              <h2 className="zepto-section-title">Electronics</h2>
            </div>
            {electronicProducts.length > 0 ? (
              <div className="row">
                {electronicProducts.map((product) => (
                  <ProductCard key={product._id} product={product} />
                ))}
              </div>
            ) : (
              <div className="col-lg-12">
                <p className="zepto-no-items">No electronics products available.</p>
              </div>
            )}
          </>
        )}

        {selectedCategory === 'Mobiles' && (
          <>
            <div className="col-lg-12 mb-4">
              <h2 className="zepto-section-title">Mobiles</h2>
            </div>
            {mobileProducts.length > 0 ? (
              <div className="row">
                {mobileProducts.map((product) => (
                  <ProductCard key={product._id} product={product} />
                ))}
              </div>
            ) : (
              <div className="col-lg-12">
                <p className="zepto-no-items">No mobile products available.</p>
              </div>
            )}
          </>
        )}

        {selectedCategory === 'Beauty' && (
          <>
            <div className="col-lg-12 mb-4">
              <h2 className="zepto-section-title">Beauty</h2>
            </div>
            {beautyProducts.length > 0 ? (
              <div className="row">
                {beautyProducts.map((product) => (
                  <ProductCard key={product._id} product={product} />
                ))}
              </div>
            ) : (
              <div className="col-lg-12">
                <p className="zepto-no-items">No beauty products available.</p>
              </div>
            )}
          </>
        )}

        {selectedCategory === 'Fashion' && (
          <>
            <div className="col-lg-12 mb-4">
              <h2 className="zepto-section-title">Fashion Products</h2>
            </div>
            {fashionProducts.length > 0 ? (
              <div className="row">
                {fashionProducts.map((product) => (
                  <ProductCard key={product._id} product={product} />
                ))}
              </div>
            ) : (
              <div className="col-lg-12">
                <p className="zepto-no-items">No fashion products available.</p>
              </div>
            )}
          </>
        )}

        {selectedCategory === 'Deal' && (
          <div className="col-lg-12">
            <p className="zepto-no-items">Deal</p>
          </div>
        )}

        {(selectedCategory === 'Baby' || selectedCategory === 'All') && (
          <>
            <div className="col-lg-12 mb-4">
              <h2 className="zepto-section-title">Baby Products</h2>
            </div>
            {BabyProducts.length > 0 ? (
              <div className="row">
                {BabyProducts.map((product) => (
                  <ProductCard key={product._id} product={product} />
                ))}
              </div>
            ) : (
              <div className="col-lg-12">
                <p className="zepto-no-items">No baby products available.</p>
              </div>
            )}
          </>
        )}
      </div>

      {selectedProduct && (
        <ProductModal product={selectedProduct} onClose={() => setSelectedProduct(null)} />
      )}
    </div>
  );
}

export default Home;
