import React, { useState } from 'react';
import { useCart } from '../contexts/cartContext';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Cart() {
  const {
    cartItems,
    addToCart,
    removeFromCart,
    clearCart,
    cartTotal,
    cartCount,
  } = useCart();
  
  const navigate = useNavigate();
  const [showAddressModal, setShowAddressModal] = useState(false);
  const [address, setAddress] = useState({
    addressLine1: '',
    houseNo: '',
    building: '',
    landmark: '',
    label: 'Home',
  });

  const discount = cartTotal * 0.1;
  const totalAfterDiscount = cartTotal - discount;

  const handleAddressChange = (e) => {
    const { name, value } = e.target;
    setAddress((prev) => ({ ...prev, [name]: value }));
  };

  const saveAddressAndProceed = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Please log in to proceed.');
        navigate('/Login');
        return;
      }

      // Save address to backend
      await axios.put(
        'http://localhost:5000/api/users/update-address',
        { address },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      // Optionally save to localStorage as well
      localStorage.setItem('userAddress', JSON.stringify(address));

      setShowAddressModal(false);
      navigate('/Payment');
    } catch (err) {
      console.error('Error saving address:', err);
      alert('Failed to save address. Please try again.');
    }
  };

  const handleProceedToCheckout = () => {
    // Check if user is logged in
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please log in to proceed.');
      navigate('/Login');
      return;
    }

    // Show address modal
    setShowAddressModal(true);
  };

  return (
    <div className="container mt-4">
      <h2>Your Cart ({cartCount} items)</h2>

      {cartItems.length === 0 ? (
        <div className="alert alert-info">
          Your cart is empty.{' '}
          <button className="btn btn-link" onClick={() => navigate('/')}>
            Continue shopping
          </button>
        </div>
      ) : (
        <>
          <div className="row">
            <div className="col-lg-8">
              {cartItems.map((item) => (
                <div key={item.ProductID} className="card mb-3">
                  <div className="row g-0">
                    <div className="col-md-4">
                      <img
                        src={`data:image/png;base64,${item.ProductImage}`}
                        className="img-fluid rounded-start"
                        alt={item.ProductName}
                      />
                    </div>
                    <div className="col-md-8">
                      <div className="card-body">
                        <h5 className="card-title">{item.ProductName}</h5>
                        <p className="card-text">₹{item.ProductPrice}</p>
                        <div className="d-flex align-items-center">
                          <button
                            className="btn btn-sm btn-outline-secondary"
                            onClick={() => removeFromCart(item.ProductID)}
                          >
                            -
                          </button>
                          <span className="mx-2">{item.quantity}</span>
                          <button
                            className="btn btn-sm btn-outline-secondary"
                            onClick={() => addToCart(item)}
                          >
                            +
                          </button>
                          <span className="ms-auto fw-bold">
                            ₹{(item.quantity * parseFloat(item.ProductPrice)).toFixed(2)}
                          </span>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>

            <div className="col-lg-4">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">Order Summary</h5>
                  <div className="d-flex justify-content-between mb-2">
                    <span>Subtotal ({cartCount} items)</span>
                    <span>₹{cartTotal.toFixed(2)}</span>
                  </div>
                  <div className="d-flex justify-content-between mb-2 text-success">
                    <span>Discount (10%)</span>
                    <span>-₹{discount.toFixed(2)}</span>
                  </div>
                  <hr />
                  <div className="d-flex justify-content-between fw-bold">
                    <span>Total</span>
                    <span>₹{totalAfterDiscount.toFixed(2)}</span>
                  </div>
                  <button
                    className="btn btn-primary w-100 mt-3"
                    onClick={handleProceedToCheckout}
                  >
                    Proceed to Checkout
                  </button>
                  <button
                    className="btn btn-outline-danger w-100 mt-2"
                    onClick={clearCart}
                  >
                    Clear Cart
                  </button>
                </div>
              </div>
            </div>
          </div>

          {/* Address Modal */}
          {showAddressModal && (
            <div
              className="modal fade show d-block"
              tabIndex="-1"
              style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}
            >
              <div className="modal-dialog modal-dialog-centered">
                <div className="modal-content">
                  <div className="modal-header">
                    <h5 className="modal-title">Enter Address Details</h5>
                    <button
                      type="button"
                      className="btn-close"
                      onClick={() => setShowAddressModal(false)}
                    ></button>
                  </div>
                  <div className="modal-body">
                    <div className="mb-3">
                      <label className="form-label">Saved Location</label>
                      <p>{localStorage.getItem('userLocation') || 'Not set'}</p>
                      <small>
                        <a href="#" style={{ color: '#ff4081' }}>
                          Change
                        </a>
                      </small>
                    </div>
                    <div className="mb-3">
                      <label className="form-label">Address Line 1 *</label>
                      <input
                        type="text"
                        className="form-control"
                        name="addressLine1"
                        value={address.addressLine1}
                        onChange={handleAddressChange}
                        placeholder="Street address"
                        required
                      />
                    </div>
                    <div className="mb-3">
                      <label className="form-label">House No. & Floor *</label>
                      <input
                        type="text"
                        className="form-control"
                        name="houseNo"
                        value={address.houseNo}
                        onChange={handleAddressChange}
                        placeholder="Enter house no. and floor"
                        required
                      />
                    </div>
                    <div className="mb-3">
                      <label className="form-label">Building & Block No. *</label>
                      <input
                        type="text"
                        className="form-control"
                        name="building"
                        value={address.building}
                        onChange={handleAddressChange}
                        placeholder="Enter building and block no."
                        required
                      />
                    </div>
                    <div className="mb-3">
                      <label className="form-label">Landmark & Area Name (Optional)</label>
                      <input
                        type="text"
                        className="form-control"
                        name="landmark"
                        value={address.landmark}
                        onChange={handleAddressChange}
                        placeholder="Enter landmark and area name"
                      />
                    </div>
                    <div className="mb-3">
                      <label className="form-label">Add Address Label</label>
                      <select
                        className="form-select"
                        name="label"
                        value={address.label}
                        onChange={handleAddressChange}
                      >
                        <option value="Home">Home</option>
                        <option value="Work">Work</option>
                        <option value="Other">Other</option>
                      </select>
                    </div>
                  </div>
                  <div className="modal-footer">
                    <button
                      type="button"
                      className="btn btn-primary w-100"
                      onClick={saveAddressAndProceed}
                    >
                      Save & Continue
                    </button>
                  </div>
                </div>
              </div>
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default Cart;
