import React from 'react';

function Cart({ cart, removeFromCart, updateQuantity }) {
    const calculateSubtotal = () => {
        return cart.reduce((sum, item) => sum + (item.ProductPrice * item.quantity), 0);
    };

    const calculateTotal = () => {
        const subtotal = calculateSubtotal();
        const tax = subtotal * 0.05; // 5% tax, adjust as needed
        return subtotal + tax;
    };

    return (
        <div className="container-fluid mt-5 shadow-lg p-4">
            <h2 className="mb-4">Shopping Cart</h2>
            {cart.length === 0 ? (
                <p>Your cart is empty</p>
            ) : (
                <>
                    {cart.map(item => (
                        <div key={item._id} className="row mb-3 align-items-center border-bottom pb-3">
                            <div className="col-lg-2">
                                <img
                                    src={`data:image/png;base64,${item.ProductImage}`}
                                    alt={item.ProductName}
                                    style={{ width: '100px', height: '100px', objectFit: 'cover' }}
                                />
                            </div>
                            <div className="col-lg-4">
                                <h5>{item.ProductName}</h5>
                                <p>Brand: {item.ProductBrand}</p>
                            </div>
                            <div className="col-lg-3">
                                <div className="d-flex align-items-center">
                                    <button
                                        className="btn btn-outline-secondary btn-sm"
                                        onClick={() => updateQuantity(item._id, item.quantity - 1)}
                                    >
                                        -
                                    </button>
                                    <span className="mx-3">{item.quantity}</span>
                                    <button
                                        className="btn btn-outline-secondary btn-sm"
                                        onClick={() => updateQuantity(item._id, item.quantity + 1)}
                                    >
                                        +
                                    </button>
                                </div>
                            </div>
                            <div className="col-lg-2">
                                <p>₹{(item.ProductPrice * item.quantity).toFixed(2)}</p>
                            </div>
                            <div className="col-lg-1">
                                <button
                                    className="btn btn-danger btn-sm"
                                    onClick={() => removeFromCart(item._id)}
                                >
                                    Remove
                                </button>
                            </div>
                        </div>
                    ))}
                    <div className="row mt-4">
                        <div className="col-lg-12 text-right">
                            <p>Subtotal: ₹{calculateSubtotal().toFixed(2)}</p>
                            <p>Tax (5%): ₹{(calculateSubtotal() * 0.05).toFixed(2)}</p>
                            <h3>Total: ₹{calculateTotal().toFixed(2)}</h3>
                        </div>
                    </div>
                </>
            )}
        </div>
    );
}

export default Cart;
