// OrderHistory.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';

const OrderHistory = ({ email }) => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    if (email) {
      axios.get(`http://localhost:5000/api/orders/history/${email}`)
        .then(res => setOrders(res.data))
        .catch(err => console.error('Error:', err));
    }
  }, [email]);

  return (
    <div className="order-history-container" style={{ padding: "20px" }}>
      <h2>ðŸ›’ Your Orders</h2>
      {orders.length === 0 ? (
        <p>No orders yet.</p>
      ) : (
        orders.map((order, idx) => (
          <div key={idx} className="order-card" style={{
            border: "1px solid #ddd",
            marginBottom: "20px",
            padding: "15px",
            borderRadius: "10px"
          }}>
            <h4>Order #{idx + 1} - â‚¹{order.total}</h4>
            <small>{new Date(order.createdAt).toLocaleString()}</small>
            <ul>
              {order.items.map((item, i) => (
                <li key={i}>
                  <strong>{item.ProductName}</strong> â€” {item.ProductQuantity} pcs @ â‚¹{item.ProductPrice}
                </li>
              ))}
            </ul>
          </div>
        ))
      )}
    </div>
  );
};

export default OrderHistory;
