import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Register() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    password: '',
    role: '',
  });

  const [otpScreen, setOtpScreen] = useState(false);
  const [generatedOtp, setGeneratedOtp] = useState('');
  const [enteredOtp, setEnteredOtp] = useState('');

  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
  };

  const validateForm = () => {
    const { name, email, phone, password, role } = formData;

    if (name.trim().length < 3) {
      alert('Name should be at least 3 characters long.');
      return false;
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.(com|in|net|org)$/;
    if (!emailRegex.test(email)) {
      alert('Please enter a valid email.');
      return false;
    }

    const phoneRegex = /^[6-9]\d{9}$/; // Indian 10-digit numbers starting from 6,7,8,9
    if (!phoneRegex.test(phone)) {
      alert('Please enter a valid 10-digit phone number.');
      return false;
    }

    const passwordRegex = /^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d@$!%*?&]{6,}$/;
    if (!passwordRegex.test(password)) {
      alert('Password must be at least 6 characters long and contain at least one letter and one number.');
      return false;
    }

    if (role === '') {
      alert('Please select a role.');
      return false;
    }

    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    // After validation, generate OTP
    const otp = Math.floor(100000 + Math.random() * 900000).toString();
    setGeneratedOtp(otp);

    // In real app, send OTP via SMS.
    // For now, just show it in alert
    alert(`Your OTP is: ${otp}`);
    setOtpScreen(true);
  };

  const handleOtpSubmit = async (e) => {
    e.preventDefault();

    if (enteredOtp !== generatedOtp) {
      alert('Invalid OTP. Please try again.');
      return;
    }

    try {
      await axios.post('http://localhost:5000/api/auth/register', {
        ...formData,
        otp: generatedOtp
      });

      alert('Registered successfully!');
      navigate('/login');
    } catch (err) {
      console.error('Register error:', err);
      alert('Registration failed. Try again!');
    }
  };

  return (
    <div className="register-container d-flex justify-content-center align-items-center">
      <div className="card p-4 shadow register-card">
        <h2 className="text-center mb-4">Register</h2>

        {!otpScreen ? (
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <input 
                name="name" 
                className="form-control" 
                placeholder="Name" 
                value={formData.name}
                onChange={handleChange}
                required
              />
            </div>
            <div className="mb-3">
              <input 
                type="email" 
                name="email" 
                className="form-control" 
                placeholder="Email" 
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>
            <div className="mb-3">
              <input 
                type="text" 
                name="phone" 
                className="form-control" 
                placeholder="Phone Number" 
                value={formData.phone}
                onChange={handleChange}
                required
              />
            </div>
            <div className="mb-3">
              <input 
                type="password" 
                name="password" 
                className="form-control" 
                placeholder="Password" 
                value={formData.password}
                onChange={handleChange}
                required
              />
            </div>
            <div className="mb-4">
              <select 
                name="role" 
                className="form-select"
                value={formData.role}
                onChange={handleChange}
                required
              >
                <option value="">Select Role</option>
                <option value="admin">Admin</option>
                <option value="vendor">Vendor</option>
                <option value="customer">Customer</option>
              </select>
            </div>
            <div className="d-grid">
              <button type="submit" className="btn btn-success">Register</button>
            </div>
          </form>
        ) : (
          <form onSubmit={handleOtpSubmit}>
            <div className="mb-3">
              <input 
                type="text" 
                className="form-control" 
                placeholder="Enter OTP" 
                value={enteredOtp}
                onChange={(e) => setEnteredOtp(e.target.value)}
                required
              />
            </div>
            <div className="d-grid">
              <button type="submit" className="btn btn-primary">Verify OTP</button>
            </div>
          </form>
        )}
      </div>
    </div>
  );
}

export default Register;
