import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AdminDashboard() {
  const [vendors, setVendors] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    vendorId: '',
    address: '',
    licenseNumber: '',
  });
  const [editId, setEditId] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchVendors();
  }, []);

  const fetchVendors = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/vendors');
      setVendors(res.data);
      setError('');
    } catch (error) {
      console.error('Error fetching vendors:', error);
      setError(error.response?.data?.message || 'Failed to fetch vendors');
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    if (!formData.name || !formData.vendorId || !formData.address || !formData.licenseNumber) {
      setError('Please fill all fields');
      return;
    }

    try {
      console.log('Submitting form with data:', formData, 'and editId:', editId);
      if (editId) {
        const updateResponse = await axios.put(`http://localhost:5000/api/vendors/${editId}`, formData);
        console.log('Vendor updated:', updateResponse.data);
        alert('Vendor updated successfully!');
        setEditId(null);
      } else {
        const createResponse = await axios.post('http://localhost:5000/api/vendors', formData);
        console.log('Vendor created:', createResponse.data);
        alert('Vendor added successfully!');
      }
      setFormData({ name: '', vendorId: '', address: '', licenseNumber: '' });
      setError('');
      fetchVendors();
    } catch (error) {
      console.error('Error submitting form:', error);
      setError(error.response?.data?.message || 'Failed to save vendor');
    }
  };

  const handleDelete = async (id) => {
    const confirmDelete = window.confirm('Are you sure you want to delete this vendor?');
    if (!confirmDelete) return;

    try {
      await axios.delete(`http://localhost:5000/api/vendors/${id}`);
      alert('Vendor deleted successfully!');
      setError('');
      fetchVendors();
    } catch (error) {
      console.error('Error deleting vendor:', error);
      setError(error.response?.data?.message || 'Failed to delete vendor');
    }
  };

  const handleEdit = (vendor) => {
    setFormData({
      name: vendor.name || '',
      vendorId: vendor.vendorId || '',
      address: vendor.address || '',
      licenseNumber: vendor.licenseNumber || '',
    });
    setEditId(vendor._id);
    setError('');
  };

  return (
    <div className="container mt-4">
      <h2>Admin Dashboard</h2>
      {error && <div className="alert alert-danger">{error}</div>}

      <div className="mb-4">
        <input
          name="name"
          placeholder="Vendor Name"
          value={formData.name}
          onChange={handleChange}
          className="form-control mb-2"
        />
        <input
          name="vendorId"
          placeholder="Vendor ID"
          value={formData.vendorId}
          onChange={handleChange}
          className="form-control mb-2"
        />
        <input
          name="address"
          placeholder="Address"
          value={formData.address}
          onChange={handleChange}
          className="form-control mb-2"
        />
        <input
          name="licenseNumber"
          placeholder="License Number"
          value={formData.licenseNumber}
          onChange={handleChange}
          className="form-control mb-2"
        />
        <button onClick={handleSubmit} className="btn btn-primary">
          {editId ? 'Update Vendor' : 'Add Vendor'}
        </button>
      </div>

      <div className="table-responsive">
        <table className="table table-bordered table-striped">
          <thead>
            <tr>
              <th>Sl No</th>
              <th>Name</th>
              <th>Vendor ID</th>
              <th>Address</th>
              <th>License Number</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {vendors.map((vendor, index) => (
              <tr key={vendor._id}>
                <td>{index + 1}</td>
                <td>{vendor.name}</td>
                <td>{vendor.vendorId}</td>
                <td>{vendor.address}</td>
                <td>{vendor.licenseNumber}</td>
                <td>
                  <button
                    onClick={() => handleEdit(vendor)}
                    className="btn btn-sm btn-warning me-2"
                  >
                    Update
                  </button>
                  <button
                    onClick={() => handleDelete(vendor._id)}
                    className="btn btn-sm btn-danger"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AdminDashboard;
