import React, { useState, useEffect } from 'react'
import axios from 'axios'
import zeptoVeg from '../images/zeptoVeggie.png'
import ZeptoPet from '../images/zeptoPetCare.png'
import ZeptoBaby from '../images/zeptoBabyCare.png'
import ZeptoHair from '../images/zeptoHaircare.png'
import ZeptoElectronics from '../images/zeptoElectronics.png'
import ZeptoIceCream from '../images/zeptoIceCream.png'
import ZeptoPackaged from '../images/zeptoPackagedFood.png'
import ZeptoCafe from '../images/zeptoCafe.png'
import ZeptoDairy from '../images/zeptoDairy.png'
import ZeptoFrozen from '../images/zeptoFrozen.png'
import ZeptoJewellery from '../images/zeptoJewellery.png' 
import ZeptoSkincare from '../images/zeptoSkinCare.png'
import ZeptoTea from '../images/zeptoTea.png'
import ZeptoToys from '../images/zeptoToys.png'
import ZeptoAtta from '../images/zeptoAtta.png'
import zetpoBanner from '../images/zeptoHomeBanner.webp'
import zeptoEleBanner from '../images/zeptoEleBanner.webp'
import zeptoBeautyBanner from '../images/zeptoBeautyBanner.webp'
import { useCart } from '../contexts/cartContext'

function Home() {
    const [selectedCategory, setSelectedCategory] = useState('All');
    const [products, setProducts] = useState([]);
    const [showSubCategory, setShowSubCategory] = useState(false);
    const [currentCategory, setCurrentCategory] = useState('');
    const [searchQuery, setSearchQuery] = useState('');
    const { addToCart } = useCart();

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const res = await axios.get('http://localhost:5000/api/customers'); 
            setProducts(res.data);
        } catch (err) {
            console.error(err);
        }
    };

    const categoryImages = {
        'Fresh': zeptoVeg,
        'Pet': ZeptoPet,
        'Baby': ZeptoBaby,
        'Hair': ZeptoHair,
        'Electronics': ZeptoElectronics,
        'IceCream': ZeptoIceCream,
        'Packaged': ZeptoPackaged,
        'Cafe': ZeptoCafe,
        'Dairy': ZeptoDairy,
        'Frozen': ZeptoFrozen,
        'Jewellery': ZeptoJewellery,
        'Skincare': ZeptoSkincare,
        'Toys': ZeptoToys,
        'Tea': ZeptoTea,
        'Atta': ZeptoAtta
    };

    const subCategories = [...new Set(products.map(product => product.ProductType))];
    
    const filteredProducts = products.filter(product => 
        product.ProductType === currentCategory &&
        product.ProductName.toLowerCase().includes(searchQuery.toLowerCase())
    );

    const electronicProducts = products.filter(product => product.ProductType === 'Electronic');
    const BabyProducts = products.filter(product => product.ProductType === 'Baby');
    const toyProducts = products.filter(product => product.ProductType === 'Toys');
    const fashionProducts = products.filter(product => product.ProductType === 'Fashion');
    const cafeProducts = products.filter(product => product.ProductType === 'Cafe');
    const homeProducts = products.filter(product => product.ProductType === 'Home');
    const mobileProducts = products.filter(product => product.ProductType === 'Mobiles');
    const beautyProducts = products.filter(product => product.ProductType === 'Beauty');

    const handleImageClick = (category) => {
        setCurrentCategory(category);
        setShowSubCategory(true);
    };

    return (
        <div className='shadow-lg'>
            <div className='container-fluid shadow-lg'>
                <div className='row'>
                    <div className='col-lg-1'></div>
                    <div className='col-lg-9 d-flex justify-content-evenly'>
                        <a href="#" className="navTags" onClick={() => setSelectedCategory('All')}>All</a>
                        <a href="#" className="navTags" onClick={() => setSelectedCategory('Cafe')}>Cafe</a>
                        <a href="#" className="navTags" onClick={() => setSelectedCategory('Home')}>Home</a>
                        <a href="#" className="navTags" onClick={() => setSelectedCategory('Toys')}>Toys</a>
                        <a href="#" className="navTags" onClick={() => setSelectedCategory('Fresh')}>Fresh</a>
                        <a href="#" className="navTags" onClick={() => setSelectedCategory('Electronics')}>Electronics</a>
                        <a href="#" className="navTags" onClick={() => setSelectedCategory('Mobiles')}>Mobiles</a>
                        <a href="#" className="navTags" onClick={() => setSelectedCategory('Beauty')}>Beauty</a>
                        <a href="#" className="navTags" onClick={() => setSelectedCategory('Fashion')}>Fashion</a>
                        <a href="#" className="navTags" onClick={() => setSelectedCategory('Deal')}>Deal Zone</a>
                        <a href="#" className="navTags" onClick={() => setSelectedCategory('Baby')}>Baby Store</a>
                    </div>
                </div>
            </div>

            <div className='row'>
                {selectedCategory === 'All' && !showSubCategory && (
                    <div className='col-lg-12'>
                        <div style={{ overflowX: 'auto', whiteSpace: 'nowrap', padding: '10px' }}>
                            {Object.entries(categoryImages).map(([category, src]) => (
                                <img 
                                    key={category}
                                    src={src} 
                                    alt={category} 
                                    style={{ width: '100px', height: '100px', marginRight: '20px', objectFit: 'contain', cursor: 'pointer' }}
                                    onClick={() => handleImageClick(category)}
                                />
                            ))}
                        </div>
                        <div className='col-lg-12'>
                            <img className='mt-5' src={zetpoBanner} alt='banner'/>
                        </div>
                        <div className="container-fluid mt-4 mb-4">
                            <div className="row">
                                <div className="col-lg-6">
                                    <img src={zeptoEleBanner} alt="banner" className="img-fluid" style={{ width: '100%', height: 'auto' }} />
                                </div>
                                <div className="col-lg-6">
                                    <img src={zeptoBeautyBanner} alt="banner" className="img-fluid" style={{ width: '100%', height: 'auto' }} />
                                </div>
                            </div>
                        </div>
                    </div>
                )}

                {showSubCategory && (
                    <div className="container-fluid">
                        <div className="row">
                            <div className="col-lg-3">
                                <div className="sidebar" style={{ height: '100vh', overflowY: 'auto', background: '#f8f9fa', padding: '20px' }}>
                                    <h4>Subcategories</h4>
                                    <ul className="list-group">
                                        {subCategories.map(subCat => (
                                            <li 
                                                key={subCat}
                                                className={`list-group-item ${currentCategory === subCat ? 'active' : ''}`}
                                                style={{ cursor: 'pointer' }}
                                                onClick={() => setCurrentCategory(subCat)}
                                            >
                                                {subCat}
                                            </li>
                                        ))}
                                    </ul>
                                    <button 
                                        className="btn btn-secondary mt-3"
                                        onClick={() => setShowSubCategory(false)}
                                    >
                                        Back to Categories
                                    </button>
                                </div>
                            </div>
                            <div className="col-lg-9">
                                <div className="p-3">
                                    <div className="input-group mb-3">
                                        <input
                                            type="text"
                                            className="form-control"
                                            placeholder="Search for products..."
                                            value={searchQuery}
                                            onChange={(e) => setSearchQuery(e.target.value)}
                                        />
                                    </div>
                                    <h2 className="text-muted">{currentCategory}</h2>
                                    <div className="row">
                                        {filteredProducts.length > 0 ? (
                                            filteredProducts.map(product => (
                                                <div key={product._id} className="col-6 col-md-4 col-lg-3 mb-4">
                                                    <div className="card h-100 border-0">
                                                        <div className="position-relative">
                                                            <img
                                                                src={`data:image/png;base64,${product.ProductImage}`}
                                                                className="card-img-top"
                                                                alt={product.ProductName}
                                                                style={{ height: '150px', objectFit: 'contain', background: '#f9f9f9' }}
                                                            />
                                                            {product.Discount && (
                                                                <span className="badge bg-danger position-absolute top-0 start-0 m-2">
                                                                    {product.Discount}% OFF
                                                                </span>
                                                            )}
                                                        </div>
                                                        <div className="card-body p-2">
                                                            <h6 className="card-title text-truncate">{product.ProductName}</h6>
                                                            <p className="text-muted small mb-1">
                                                                ₹{Number(product.ProductPrice).toFixed(2)} 
                                                                <span className="text-decoration-line-through text-secondary">
                                                                    ₹{(Number(product.ProductPrice) * 1.2).toFixed(2)}
                                                                </span>
                                                            </p>
                                                            <button 
                                                                className="btn btn-outline-success btn-sm w-100"
                                                                onClick={() => addToCart(product)}
                                                            >
                                                                Add to Cart
                                                            </button>
                                                            <p className="text-success small mt-1">7 Mins</p>
                                                        </div>
                                                    </div>
                                                </div>
                                            ))
                                        ) : (
                                            <div className="col-12">
                                                <p className="text-center">No products available in this category.</p>
                                            </div>
                                        )}
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                )}

                {(selectedCategory === 'Cafe' || selectedCategory === 'All') && !showSubCategory && (
                    <>
                        <div className='col-lg-12 mb-4'>
                            <h2>Cafe Products</h2>
                        </div>
                        {cafeProducts.length > 0 ? (
                            <div className="row">
                                {cafeProducts.map((product) => (
                                    <div key={product._id} className="col-lg-3">
                                        <div className="card h-100">
                                            <img
                                                src={`data:image/png;base64,${product.ProductImage}`}
                                                className="card-img-top"
                                                alt={product.ProductName}
                                                style={{ height: '200px', objectFit: 'cover' }}
                                            />
                                            <div className="card-body">
                                                <h5 className="card-title">{product.ProductName}</h5>
                                                <p className="card-text">Brand: {product.ProductBrand}</p>
                                                <p className="card-text">Price: ₹{product.ProductPrice}</p>
                                                <button 
                                                    className="btn btn-primary"
                                                    onClick={() => addToCart(product)}
                                                >
                                                    Add to Cart
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <div className="col-lg-12">
                                <p>No cafe products available.</p>
                            </div>
                        )}
                    </>
                )}

                {selectedCategory === 'Home' && !showSubCategory && (
                    <>
                        <div className='col-lg-12 mb-4'>
                            <h2>Home Products</h2>
                        </div>
                        {homeProducts.length > 0 ? (
                            <div className="row">
                                {homeProducts.map((product) => (
                                    <div key={product._id} className="col-lg-3">
                                        <div className="card h-100">
                                            <img
                                                src={`data:image/png;base64,${product.ProductImage}`}
                                                className="card-img-top"
                                                alt={product.ProductName}
                                                style={{ height: '200px', objectFit: 'cover' }}
                                            />
                                            <div className="card-body">
                                                <h5 className="card-title">{product.ProductName}</h5>
                                                <p className="card-text">Brand: {product.ProductBrand}</p>
                                                <p className="card-text">Price: ₹{product.ProductPrice}</p>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <div className="col-lg-12">
                                <p>No home products available.</p>
                            </div>
                        )}
                    </>
                )}

                {selectedCategory === 'Toys' && !showSubCategory && (
                    <>
                        <div className='col-lg-12 mb-4'>
                            <h2>Toy Products</h2>
                        </div>
                        {toyProducts.length > 0 ? (
                            <div className="row">
                                {toyProducts.map((product) => (
                                    <div key={product._id} className="col-lg-3">
                                        <div className="card h-100">
                                            <img
                                                src={`data:image/png;base64,${product.ProductImage}`}
                                                className="card-img-top"
                                                alt={product.ProductName}
                                                style={{ height: '200px', objectFit: 'cover' }}
                                            />
                                            <div className="card-body">
                                                <h5 className="card-title">{product.ProductName}</h5>
                                                <p className="card-text">Brand: {product.ProductBrand}</p>
                                                <p className="card-text">Price: ₹{product.ProductPrice}</p>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <div className="col-lg-12">
                                <p>No toy products available.</p>
                            </div>
                        )}
                    </>
                )}

                {selectedCategory === 'Fresh' && !showSubCategory && (
                    <div className='col-lg-12'>
                        Fresh
                    </div>
                )}

                {(selectedCategory === 'Electronics' || selectedCategory === 'All') && !showSubCategory && (
                    <>
                        <div className='col-lg-12 mb-4'>
                            <h2>Electronics</h2>
                        </div>
                        {electronicProducts.length > 0 ? (
                            <div className="row">
                                {electronicProducts.map((product) => (
                                    <div key={product._id} className="col-lg-3">
                                        <div className="card h-100">
                                            <img
                                                src={`data:image/png;base64,${product.ProductImage}`}
                                                className="card-img-top"
                                                alt={product.ProductName}
                                                style={{ height: '200px', objectFit: 'cover' }}
                                            />
                                            <div className="card-body">
                                                <h5 className="card-title">{product.ProductName}</h5>
                                                <p className="card-text">Brand: {product.ProductBrand}</p>
                                                <p className="card-text">Price: ₹{product.ProductPrice}</p>
                                                <button 
                                                    className="btn btn-primary"
                                                    onClick={() => addToCart(product)}
                                                >
                                                    Add to Cart
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <div className="col-lg-12">
                                <p>No electronics products available.</p>
                            </div>
                        )}
                    </>
                )}

                {selectedCategory === 'Mobiles' && !showSubCategory && (
                    <>
                        <div className='col-lg-12 mb-4'>
                            <h2>Mobiles</h2>
                        </div>
                        {mobileProducts.length > 0 ? (
                            <div className="row">
                                {mobileProducts.map((product) => (
                                    <div key={product._id} className="col-lg-3">
                                        <div className="card h-100">
                                            <img
                                                src={`data:image/png;base64,${product.ProductImage}`}
                                                className="card-img-top"
                                                alt={product.ProductName}
                                                style={{ height: '200px', objectFit: 'cover' }}
                                            />
                                            <div className="card-body">
                                                <h5 className="card-title">{product.ProductName}</h5>
                                                <p className="card-text">Brand: {product.ProductBrand}</p>
                                                <p className="card-text">Price: ₹{product.ProductPrice}</p>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <div className="col-lg-12">
                                <p>No mobile products available.</p>
                            </div>
                        )}
                    </>
                )}

                {selectedCategory === 'Beauty' && !showSubCategory && (
                    <>
                        <div className='col-lg-12 mb-4'>
                            <h2>Beauty</h2>
                        </div>
                        {beautyProducts.length > 0 ? (
                            <div className="row">
                                {beautyProducts.map((product) => (
                                    <div key={product._id} className="col-lg-3">
                                        <div className="card h-100">
                                            <img
                                                src={`data:image/png;base64,${product.ProductImage}`}
                                                className="card-img-top"
                                                alt={product.ProductName}
                                                style={{ height: '200px', objectFit: 'cover' }}
                                            />
                                            <div className="card-body">
                                                <h5 className="card-title">{product.ProductName}</h5>
                                                <p className="card-text">Brand: {product.ProductBrand}</p>
                                                <p className="card-text">Price: ₹{product.ProductPrice}</p>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <div className="col-lg-12">
                                <p>No beauty products available.</p>
                            </div>
                        )}
                    </>
                )}

                {selectedCategory === 'Fashion' && !showSubCategory && (
                    <>
                        <div className='col-lg-12 mb-4'>
                            <h2>Fashion Products</h2>
                        </div>
                        {fashionProducts.length > 0 ? (
                            <div className="row">
                                {fashionProducts.map((product) => (
                                    <div key={product._id} className="col-lg-3">
                                        <div className="card h-100">
                                            <img
                                                src={`data:image/png;base64,${product.ProductImage}`}
                                                className="card-img-top"
                                                alt={product.ProductName}
                                                style={{ height: '200px', objectFit: 'cover' }}
                                            />
                                            <div className="card-body">
                                                <h5 className="card-title">{product.ProductName}</h5>
                                                <p className="card-text">Brand: {product.ProductBrand}</p>
                                                <p className="card-text">Price: ₹{product.ProductPrice}</p>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <div className="col-lg-12">
                                <p>No fashion products available.</p>
                            </div>
                        )}
                    </>
                )}

                {selectedCategory === 'Deal' && !showSubCategory && (
                    <div className='col-lg-12'>
                        Deal
                    </div>
                )}

                {(selectedCategory === 'Baby' || selectedCategory === 'All') && !showSubCategory && (
                    <>
                        <div className='col-lg-12 mb-4'>
                            <h2>Baby Products</h2>
                        </div>
                        {BabyProducts.length > 0 ? (
                            <div className="row">
                                {BabyProducts.map((product) => (
                                    <div key={product._id} className="col-lg-3">
                                        <div className="card h-100">
                                            <img
                                                src={`data:image/png;base64,${product.ProductImage}`}
                                                className="card-img-top"
                                                alt={product.ProductName}
                                                style={{ height: '200px', objectFit: 'cover' }}
                                            />
                                            <div className="card-body">
                                                <h5 className="card-title">{product.ProductName}</h5>
                                                <p className="card-text">Brand: {product.ProductBrand}</p>
                                                <p className="card-text">Price: ₹{product.ProductPrice}</p>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <div className="col-lg-12">
                                <p>No baby products available.</p>
                            </div>
                        )}
                    </>
                )}
            </div>
        </div>
    )
}

export default Home
