import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/category.css';

function CategoryPage() {
  const { categoryName } = useParams(); // Get category name from URL
  const navigate = useNavigate();
  const [subcategories, setSubcategories] = useState([]); // State for dynamic subcategories
  const [loading, setLoading] = useState(true); // Loading state
  const [error, setError] = useState(null); // Error state

  useEffect(() => {
    const fetchSubcategories = async () => {
      try {
        setLoading(true);
        const res = await axios.get('http://localhost:5000/api/customers');
        const products = res.data;

        // Filter products by ProductType and extract unique ProductSubType values
        const filteredSubcategories = [
          ...new Set(
            products
              .filter((product) => product.ProductType === categoryName)
              .map((product) => product.ProductSubType)
          ),
        ];

        setSubcategories(filteredSubcategories);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching subcategories:', err);
        setError('Failed to load subcategories. Please try again.');
        setLoading(false);
      }
    };

    fetchSubcategories();
  }, [categoryName]); // Re-fetch when categoryName changes

  return (
    <div className="category-page">
      <div className="zepto-sidebar">
        <div className="zepto-sidebar-header">
          <h5>{categoryName} Subcategories</h5>
          <button className="btn btn-close" onClick={() => navigate('/')}></button>
        </div>
        <div className="zepto-sidebar-body">
          {loading ? (
            <p>Loading subcategories...</p>
          ) : error ? (
            <p className="zepto-sidebar-error">{error}</p>
          ) : (
            <ul>
              {subcategories.length > 0 ? (
                subcategories.map((subcategory, index) => (
                  <li key={index} className="zepto-sidebar-item">
                    {subcategory}
                  </li>
                ))
              ) : (
                <li className="zepto-sidebar-item">No subcategories available</li>
              )}
            </ul>
          )}
        </div>
      </div>
      <div className="category-content">
        <h2>{categoryName}</h2>
        <p>Products for {categoryName} will be displayed here.</p>
        {/* Add product listing or other content here if needed */}
      </div>
    </div>
  );
}

export default CategoryPage;
